'use strict';

import * as ts from 'typescript';
import { TypeNode, PropertyInfo, AnalyzeResult, AnalyzeError } from './types';
import { TypeGraphImpl } from './graph';

/**
 * AST Analyzer for finding Mnemonica define() and decorate() calls
 */
export class MnemonicaAnalyzer {
	private errors: AnalyzeError[] = [];
	private graph = new TypeGraphImpl();

	constructor(program?: ts.Program) {
		// Store program for future use (currently unused but kept for extensibility)
		void program;
	}

	/**
	 * Analyze a source file for Mnemonica type definitions
	 */
	analyzeFile(sourceFile: ts.SourceFile): AnalyzeResult {
		this.errors = [];
		this.visitNode(sourceFile, sourceFile);

		return {
			types: this.graph.getAllTypes(),
			errors: this.errors,
		};
	}

	/**
	 * Analyze source code string
	 */
	analyzeSource(sourceCode: string, fileName = 'temp.ts'): AnalyzeResult {
		const sourceFile = ts.createSourceFile(
			fileName,
			sourceCode,
			ts.ScriptTarget.Latest,
			true
		);
		return this.analyzeFile(sourceFile);
	}

	/**
	 * Get the type graph
	 */
	getGraph(): TypeGraphImpl {
		return this.graph;
	}

	/**
	 * Visit a node in the AST
	 */
	private visitNode(node: ts.Node, sourceFile: ts.SourceFile, currentClass?: ts.ClassDeclaration): void {
		// Check for define() calls
		if (this.isDefineCall(node)) {
			this.processDefineCall(node as ts.CallExpression, sourceFile);
		}

		// Check for decorate() decorator
		if (this.isDecorateDecorator(node)) {
			this.processDecorateDecorator(node as ts.Decorator, sourceFile, currentClass);
		}

		// Track class declarations for decorator parent lookup
		if (ts.isClassDeclaration(node)) {
			// Visit children with this class as the current context
			ts.forEachChild(node, child => this.visitNode(child, sourceFile, node));
		} else {
			// Recursively visit children
			ts.forEachChild(node, child => this.visitNode(child, sourceFile, currentClass));
		}
	}

	/**
	 * Check if a node is a define() call
	 */
	private isDefineCall(node: ts.Node): node is ts.CallExpression {
		if (!ts.isCallExpression(node)) {
			return false;
		}

		const expression = node.expression;

		// Check for direct call: define('TypeName', ...)
		if (ts.isIdentifier(expression) && expression.text === 'define') {
			return true;
		}

		// Check for method call: SomeType.define('SubType', ...)
		if (ts.isPropertyAccessExpression(expression)) {
			return expression.name?.text === 'define';
		}

		return false;
	}

	/**
	 * Check if a node is a @decorate() decorator
	 */
	private isDecorateDecorator(node: ts.Node): node is ts.Decorator {
		if (!ts.isDecorator(node)) {
			return false;
		}

		const expression = node.expression;

		// Check for @decorate
		if (ts.isIdentifier(expression) && expression.text === 'decorate') {
			return true;
		}

		// Check for @decorate() or @decorate(ParentType)
		if (ts.isCallExpression(expression)) {
			const fnName = expression.expression;
			if (ts.isIdentifier(fnName) && fnName.text === 'decorate') {
				return true;
			}
		}

		return false;
	}

	/**
	 * Process a define() call
	 */
	private processDefineCall(call: ts.CallExpression, sourceFile: ts.SourceFile): void {
		const { line, character } = ts.getLineAndCharacterOfPosition(
			sourceFile,
			call.getStart(sourceFile)
		);

		// Get the type name from arguments
		const typeName = this.extractTypeName(call);
		if (!typeName) {
			this.errors.push({
				message: 'Could not extract type name from define() call',
				file: sourceFile.fileName,
				line: line + 1,
				column: character + 1,
			});
			return;
		}

		// Determine parent type
		const parentNode = this.findParentType(call);

		// Create type node
		const node = TypeGraphImpl.createNode(
			typeName,
			parentNode,
			sourceFile.fileName,
			line + 1,
			character + 1
		);

		// Extract properties from constructor function
		node.properties = this.extractProperties(call);

		// Add to graph
		if (parentNode) {
			this.graph.addChild(parentNode, node);
		} else {
			this.graph.addRoot(node);
		}
	}

	/**
	 * Process a @decorate() decorator
	 */
	private processDecorateDecorator(decorator: ts.Decorator, sourceFile: ts.SourceFile, classDeclParam?: ts.ClassDeclaration): void {
		const { line, character } = ts.getLineAndCharacterOfPosition(
			sourceFile,
			decorator.getStart(sourceFile)
		);

		// Get the class declaration - use the passed context if parent is not set
		const classDecl = decorator.parent as ts.ClassDeclaration | undefined || classDeclParam;
		if (!classDecl || !classDecl.name) {
			this.errors.push({
				message: 'Decorated class has no name',
				file: sourceFile.fileName,
				line: line + 1,
				column: character + 1,
			});
			return;
		}

		const typeName = classDecl.name.text;
		if (!typeName) {
			this.errors.push({
				message: 'Decorated class has no name',
				file: sourceFile.fileName,
				line: line + 1,
				column: character + 1,
			});
			return;
		}

		// Try to find parent type from decorator arguments
		let parentNode: TypeNode | undefined;
		if (ts.isCallExpression(decorator.expression)) {
			const args = decorator.expression.arguments;
			if (args.length > 0 && ts.isIdentifier(args[0])) {
				const parentName = args[0].text;
				// First try exact match
				parentNode = this.graph.findType(parentName);
				// Then search through all types for one with matching name
				if (!parentNode) {
					for (const type of this.graph.getAllTypes()) {
						if (type.name === parentName) {
							parentNode = type;
							break;
						}
					}
				}
			}
		}

		// Create type node
		const node = TypeGraphImpl.createNode(
			typeName,
			parentNode,
			sourceFile.fileName,
			line + 1,
			character + 1
		);

		// Extract properties from class members
		node.properties = this.extractClassProperties(classDecl);

		// Add to graph
		if (parentNode) {
			this.graph.addChild(parentNode, node);
		} else {
			this.graph.addRoot(node);
		}
	}

	/**
	 * Extract type name from define() call arguments
	 */
	private extractTypeName(call: ts.CallExpression): string | undefined {
		const args = call.arguments;

		// define('TypeName', handler) or define(() => class)
		if (args.length === 0) {
			return undefined;
		}

		const firstArg = args[0];

		// String literal: define('TypeName', ...)
		if (ts.isStringLiteral(firstArg)) {
			return firstArg.text;
		}

		// Function with name: define(function TypeName() {})
		if (ts.isFunctionExpression(firstArg) && firstArg.name) {
			return firstArg.name.text;
		}

		// Arrow function returning class: define(() => class TypeName {})
		if (ts.isArrowFunction(firstArg)) {
			const body = firstArg.body;
			if (ts.isClassExpression(body) && body.name) {
				return body.name.text;
			}
		}

		return undefined;
	}

	/**
	 * Find parent type node for nested define calls
	 */
	private findParentType(call: ts.CallExpression): TypeNode | undefined {
		const expression = call.expression;

		// Check for method call: SomeType.define('SubType', ...)
		if (!ts.isPropertyAccessExpression(expression)) {
			return undefined;
		}

		// Get the object being called on (SomeType in SomeType.define)
		const obj = expression.expression;

		if (ts.isIdentifier(obj)) {
			// Direct reference to parent type - search by simple name
			const name = obj.text;
			// First try exact match
			const exact = this.graph.findType(name);
			if (exact) return exact;
			// Then search through all types for one with matching name
			for (const type of this.graph.getAllTypes()) {
				if (type.name === name) {
					return type;
				}
			}
			return undefined;
		}

		if (ts.isPropertyAccessExpression(obj)) {
			// Nested access: instance.Type.define - try to resolve
			const chain = this.getPropertyChain(obj);
			if (chain.length > 0) {
				return this.graph.findType(chain.join('.'));
			}
		}

		return undefined;
	}

	/**
	 * Get property chain from nested access
	 */
	private getPropertyChain(expr: ts.PropertyAccessExpression | ts.Identifier): string[] {
		const chain: string[] = [];

		let current: ts.Expression = expr;
		while (ts.isPropertyAccessExpression(current)) {
			if (current.name) {
				chain.unshift(current.name.text);
			}
			current = current.expression;
		}

		if (ts.isIdentifier(current)) {
			chain.unshift(current.text);
		}

		return chain;
	}

	/**
	 * Extract properties from constructor function
	 */
	private extractProperties(call: ts.CallExpression): Map<string, PropertyInfo> {
		const properties = new Map<string, PropertyInfo>();

		const handlerArg = call.arguments[1];
		if (!handlerArg) {
			return properties;
		}

		// Handle function expression
		if (ts.isFunctionExpression(handlerArg) || ts.isArrowFunction(handlerArg)) {
			const body = handlerArg.body;

			// Function body with statements
			if (ts.isBlock(body)) {
				for (const stmt of body.statements) {
					if (ts.isExpressionStatement(stmt)) {
						this.extractPropertyFromStatement(stmt.expression, properties);
					}
				}
			}
		}

		// Handle class expression
		if (ts.isClassExpression(handlerArg)) {
			for (const member of handlerArg.members) {
				if (ts.isPropertyDeclaration(member) && member.name) {
					const name = ts.isIdentifier(member.name) ? member.name.text : '';
					if (name) {
						properties.set(name, {
							name,
							type: this.inferType(member.type),
							optional: !!member.questionToken,
						});
					}
				}
			}
		}

		return properties;
	}

	/**
	 * Extract property assignment from statement
	 */
	private extractPropertyFromStatement(
		expr: ts.Expression,
		properties: Map<string, PropertyInfo>
	): void {
		// Handle: this.property = value
		if (ts.isBinaryExpression(expr) &&
			expr.operatorToken.kind === ts.SyntaxKind.EqualsToken) {
			const left = expr.left;

			if (ts.isPropertyAccessExpression(left)) {
				// Check if accessing 'this' (ThisKeyword)
				if (left.expression.kind === ts.SyntaxKind.ThisKeyword) {
					const name = left.name?.text;
					if (name) {
						properties.set(name, {
							name,
							type: this.inferTypeFromInitializer(expr.right),
							optional: false,
						});
					}
				}
			}
		}

		// Handle: Object.assign(this, { prop: value })
		if (ts.isCallExpression(expr)) {
			const fn = expr.expression;
			if (ts.isPropertyAccessExpression(fn) &&
				fn.name?.text === 'assign' &&
				ts.isIdentifier(fn.expression) &&
				fn.expression.text === 'Object') {
				const args = expr.arguments;
				if (args.length >= 2 && args[0].kind === ts.SyntaxKind.ThisKeyword) {
					// Extract properties from the second argument
					const propsArg = args[1];
					if (ts.isObjectLiteralExpression(propsArg)) {
						for (const prop of propsArg.properties) {
							if (ts.isPropertyAssignment(prop) && ts.isIdentifier(prop.name)) {
								const name = prop.name.text;
								properties.set(name, {
									name,
									type: this.inferTypeFromInitializer(prop.initializer),
									optional: false,
								});
							}
						}
					}
				}
			}
		}
	}

	/**
	 * Extract properties from class declaration
	 */
	private extractClassProperties(classDecl: ts.ClassDeclaration): Map<string, PropertyInfo> {
		const properties = new Map<string, PropertyInfo>();

		for (const member of classDecl.members) {
			if (ts.isPropertyDeclaration(member) && member.name) {
				const name = ts.isIdentifier(member.name) ? member.name.text : '';
				if (name) {
					properties.set(name, {
						name,
						type: this.inferType(member.type),
						optional: !!member.questionToken,
					});
				}
			}
		}

		return properties;
	}

	/**
	 * Infer TypeScript type from type node
	 */
	private inferType(typeNode?: ts.TypeNode): string {
		if (!typeNode) {
			return 'unknown';
		}

		switch (typeNode.kind) {
			case ts.SyntaxKind.StringKeyword:
				return 'string';
			case ts.SyntaxKind.NumberKeyword:
				return 'number';
			case ts.SyntaxKind.BooleanKeyword:
				return 'boolean';
			case ts.SyntaxKind.UndefinedKeyword:
				return 'undefined';
			case ts.SyntaxKind.NullKeyword:
				return 'null';
			case ts.SyntaxKind.AnyKeyword:
				return 'any';
			case ts.SyntaxKind.UnknownKeyword:
				return 'unknown';
			case ts.SyntaxKind.VoidKeyword:
				return 'void';
			case ts.SyntaxKind.ArrayType:
				return 'Array<' + this.inferType((typeNode as ts.ArrayTypeNode).elementType) + '>';
			case ts.SyntaxKind.TypeLiteral:
				return 'object';
			default:
				// For complex types, return the text representation
				return 'unknown';
		}
	}

	/**
	 * Infer type from initializer
	 */
	private inferTypeFromInitializer(initializer: ts.Expression): string {
		switch (initializer.kind) {
			case ts.SyntaxKind.StringLiteral:
				return 'string';
			case ts.SyntaxKind.NumericLiteral:
				return 'number';
			case ts.SyntaxKind.TrueKeyword:
			case ts.SyntaxKind.FalseKeyword:
				return 'boolean';
			case ts.SyntaxKind.NullKeyword:
				return 'null';
			case ts.SyntaxKind.UndefinedKeyword:
				return 'undefined';
			case ts.SyntaxKind.ArrayLiteralExpression:
				return 'Array<unknown>';
			case ts.SyntaxKind.ObjectLiteralExpression:
				return 'object';
			case ts.SyntaxKind.NewExpression: {
				// Handle new Date(), new Map(), etc.
				const newExpr = initializer as ts.NewExpression;
				if (ts.isIdentifier(newExpr.expression)) {
					return newExpr.expression.text;
				}
				return 'object';
			}
			default:
				return 'unknown';
		}
	}
}

'use strict';

import { TypeNode, TypeGraph } from './types';

/**
 * Trie-based type graph for storing Mnemonica type hierarchy
 */
export class TypeGraphImpl implements TypeGraph {
	roots: Map<string, TypeNode> = new Map();
	allTypes: Map<string, TypeNode> = new Map();

	addRoot(node: TypeNode): void {
		this.roots.set(node.name, node);
		this.allTypes.set(node.fullPath, node);
	}

	addChild(parent: TypeNode, child: TypeNode): void {
		parent.children.set(child.name, child);
		child.parent = parent;
		this.allTypes.set(child.fullPath, child);
	}

	findType(fullPath: string): TypeNode | undefined {
		return this.allTypes.get(fullPath);
	}

	getAllTypes(): TypeNode[] {
		return Array.from(this.allTypes.values());
	}

	clear(): void {
		this.roots.clear();
		this.allTypes.clear();
	}

	/**
	 * Create a new TypeNode
	 */
	static createNode(
		name: string,
		parent: TypeNode | undefined,
		sourceFile: string,
		line: number,
		column: number
	): TypeNode {
		const fullPath = parent ? `${parent.fullPath}.${name}` : name;
		return {
			name,
			fullPath,
			properties: new Map(),
			parent,
			children: new Map(),
			sourceFile,
			line,
			column,
		};
	}

	/**
	 * Traverse the graph in breadth-first order
	 */
	*bfs(): Generator<TypeNode> {
		const visited = new Set<string>();
		const queue: TypeNode[] = Array.from(this.roots.values());

		while (queue.length > 0) {
			const node = queue.shift()!;
			if (visited.has(node.fullPath)) {
				continue;
			}
			visited.add(node.fullPath);
			yield node;

			for (const child of node.children.values()) {
				queue.push(child);
			}
		}
	}

	/**
	 * Traverse the graph in depth-first order
	 */
	*dfs(node?: TypeNode, visited = new Set<string>()): Generator<TypeNode> {
		const startNode = node || this.roots.values().next().value;
		if (!startNode || visited.has(startNode.fullPath)) {
			return;
		}

		visited.add(startNode.fullPath);
		yield startNode;

		for (const child of startNode.children.values()) {
			yield* this.dfs(child, visited);
		}
	}
}

'use strict';

import { TypeNode, GeneratedTypes } from './types';
import { TypeGraphImpl } from './graph';

/**
 * TypeScript declaration file generator
 */
export class TypesGenerator {
	constructor(private graph: TypeGraphImpl) {}

	/**
	 * Generate the complete .d.ts file content for module augmentation
	 * This adds nested constructors to mnemonica's instance types
	 */
	generate(): GeneratedTypes {
		const lines: string[] = [];
		const generatedTypes: string[] = [];

		// Header
		lines.push('// Generated by @mnemonica/tactica - DO NOT EDIT');
		lines.push('// This file provides TypeScript types for Mnemonica nested constructors');
		lines.push('');
		lines.push('/* eslint-disable @typescript-eslint/no-empty-object-type */');
		lines.push('/* eslint-disable @typescript-eslint/no-empty-interface */');
		lines.push('');

		// Generate instance type augmentations
		lines.push('// Augmented instance types with nested constructors');
		lines.push('declare module "mnemonica" {');

		// Generate instance interfaces with nested constructors
		for (const root of this.graph.roots.values()) {
			for (const node of this.graph.dfs(root)) {
				const interfaceName = `${node.name}Instance`;
				generatedTypes.push(interfaceName);
				this.generateInstanceInterface(node, lines, 1);
			}
		}

		lines.push('}');

		return {
			content: lines.join('\n'),
			types: generatedTypes,
		};
	}

	/**
	 * Generate a types.ts file with complete instance interfaces
	 * This includes all properties extracted from the constructors
	 */
	generateTypesFile(): GeneratedTypes {
		const lines: string[] = [];
		const generatedTypes: string[] = [];

		// Header
		lines.push('// Generated by @mnemonica/tactica - DO NOT EDIT');
		lines.push('// This file provides complete TypeScript interfaces for Mnemonica types');
		lines.push('');
		lines.push('/* eslint-disable @typescript-eslint/no-empty-object-type */');
		lines.push('/* eslint-disable @typescript-eslint/no-empty-interface */');
		lines.push('');

		// Define a helper type for nested constructors (since IDefinitorInstance is internal)
		lines.push('// Helper type for nested type constructors');
		lines.push('type TypeConstructor<T> = {');
		lines.push('\tnew (...args: unknown[]): T;');
		lines.push('\t(...args: unknown[]): T;');
		lines.push('};');
		lines.push('');

		// Generate complete instance interfaces
		for (const root of this.graph.roots.values()) {
			for (const node of this.graph.dfs(root)) {
				const interfaceName = `${node.name}Instance`;
				generatedTypes.push(interfaceName);
				this.generateCompleteInstanceInterface(node, lines);
			}
		}

		return {
			content: lines.join('\n'),
			types: generatedTypes,
		};
	}

	/**
	 * Generate an instance interface for a type node
	 */
	private generateInstanceInterface(node: TypeNode, lines: string[], indent: number): void {
		const indentStr = '\t'.repeat(indent);
		const interfaceName = `${node.name}Instance`;

		// Build extends clause
		const extendsTypes: string[] = [];
		if (node.parent) {
			extendsTypes.unshift(`${node.parent.name}Instance`);
		}

		const extendsClause = extendsTypes.length > 0
			? ` extends ${extendsTypes.join(', ')}`
			: '';

		lines.push(`${indentStr}interface ${interfaceName}${extendsClause} {`);

		// Add nested constructor properties
		for (const child of node.children.values()) {
			const childInstanceType = `${child.name}Instance`;
			lines.push(`${indentStr}\t${child.name}: {`);
			lines.push(`${indentStr}\t\tnew (...args: unknown[]): ${childInstanceType};`);
			lines.push(`${indentStr}\t\t(...args: unknown[]): ${childInstanceType};`);
			lines.push(`${indentStr}\t};`);
		}

		// For module augmentation, we don't add instance properties
		// because they should already be defined in the source

		lines.push(`${indentStr}}`);
		lines.push('');
	}

	/**
	 * Generate a complete instance interface with all properties
	 * This is for the types.ts file that users import from
	 */
	private generateCompleteInstanceInterface(node: TypeNode, lines: string[]): void {
		const interfaceName = `${node.name}Instance`;

		// Build extends clause
		const extendsTypes: string[] = [];
		if (node.parent) {
			extendsTypes.unshift(`${node.parent.name}Instance`);
		}

		const extendsClause = extendsTypes.length > 0
			? ` extends ${extendsTypes.join(', ')}`
			: '';

		// Export the interface
		lines.push(`export interface ${interfaceName}${extendsClause} {`);

		// Add instance properties extracted from the constructor
		for (const [propName, propInfo] of node.properties.entries()) {
			const optional = propInfo.optional ? '?' : '';
			lines.push(`\t${propName}${optional}: ${propInfo.type};`);
		}

		// Add nested constructor properties
		for (const child of node.children.values()) {
			const childInstanceType = `${child.name}Instance`;
			lines.push(`\t${child.name}: TypeConstructor<${childInstanceType}>;`);
		}

		lines.push('}');
		lines.push('');
	}

	/**
	 * Generate a simple type declaration for a single type
	 */
	generateSingleType(node: TypeNode): string {
		const lines: string[] = [];
		this.generateInstanceInterface(node, lines, 0);
		return lines.join('\n');
	}
}

'use strict';

import * as fs from 'fs';
import * as path from 'path';
import { GeneratedTypes } from './types';

/**
 * File writer for generated types
 */
export class TypesWriter {
	private outputDir: string;

	constructor(outputDir = '.mnemonica') {
		this.outputDir = outputDir;
	}

	/**
	 * Write generated types to file
	 */
	write(generated: GeneratedTypes): string {
		// Ensure output directory exists
		this.ensureDirectory();

		const outputPath = path.join(this.outputDir, 'types.d.ts');

		// Write the file
		fs.writeFileSync(outputPath, generated.content, 'utf-8');

		// Update .gitignore if needed
		this.updateGitignore();

		return outputPath;
	}

	/**
	 * Write types.ts file with complete interfaces
	 */
	writeTypesFile(generated: GeneratedTypes): string {
		this.ensureDirectory();

		const outputPath = path.join(this.outputDir, 'types.ts');
		fs.writeFileSync(outputPath, generated.content, 'utf-8');

		return outputPath;
	}

	/**
	 * Write types with custom filename
	 */
	writeTo(filename: string, content: string): string {
		this.ensureDirectory();
		const outputPath = path.join(this.outputDir, filename);
		fs.writeFileSync(outputPath, content, 'utf-8');
		return outputPath;
	}

	/**
	 * Ensure output directory exists
	 */
	private ensureDirectory(): void {
		if (!fs.existsSync(this.outputDir)) {
			fs.mkdirSync(this.outputDir, { recursive: true });
		}
	}

	/**
	 * Update .gitignore to exclude .mnemonica folder
	 */
	private updateGitignore(): void {
		const gitignorePath = path.join(process.cwd(), '.gitignore');

		if (!fs.existsSync(gitignorePath)) {
			// Create .gitignore
			fs.writeFileSync(gitignorePath, `${this.outputDir}/\n`, 'utf-8');
			return;
		}

		const content = fs.readFileSync(gitignorePath, 'utf-8');
		const lines = content.split('\n');

		// Check if already ignored
		const isIgnored = lines.some(line =>
			line.trim() === this.outputDir ||
			line.trim() === `${this.outputDir}/`
		);

		if (!isIgnored) {
			// Add to .gitignore
			const newContent = content.endsWith('\n')
				? `${content}${this.outputDir}/\n`
				: `${content}\n${this.outputDir}/\n`;
			fs.writeFileSync(gitignorePath, newContent, 'utf-8');
		}
	}

	/**
	 * Clean the output directory
	 */
	clean(): void {
		if (fs.existsSync(this.outputDir)) {
			const files = fs.readdirSync(this.outputDir);
			for (const file of files) {
				fs.unlinkSync(path.join(this.outputDir, file));
			}
		}
	}

	/**
	 * Get the output directory path
	 */
	getOutputDir(): string {
		return this.outputDir;
	}
}
